import { Component } from '@angular/core';
import { ForumService } from 'src/app/components/forum/forum.service';
import { Router } from '@angular/router';
import { UserService } from 'src/app/components/login/user.service';

@Component({
  selector: 'app-post-probleme',
  templateUrl: './post-probleme.component.html',
  styleUrls: ['./post-probleme.component.css']
})
export class PostProblemeComponent {

  problem: any = {
    objet: '',
    autreProbleme: '',
    image: '',
    resume: '',
    date: new Date(),
    auteur: this.userService.utilisateurConnecte?.pseudo || "anonymous",
  };

  constructor(
    private forumService: ForumService,
    private router: Router,
    private userService: UserService
  ) {}

  submitProblem() {
    const utilisateurConnecte = this.userService.utilisateurConnecte;
    if (utilisateurConnecte) {
      this.problem.auteur = utilisateurConnecte.pseudo;
    }

    this.forumService.addProblem(this.problem);
    this.router.navigate(['/forum']);
  }

  onFileChange(event: any) {
    // Logique pour gérer le changement de fichier
  }
}
<main>
  <section class="rhr">
    <h2 class="remplir">Postez votre problème</h2>
    <hr>
  </section>
  <figure class="form" role="form">
    <form (ngSubmit)="submitProblem()">
      <fieldset>
        <label for="objet">Quel est le problème rencontré sur votre vélo ?</label>
        <select name="objet" id="objet" [(ngModel)]="problem.objet" required>
          <option value="">- Choisissez le problème -</option>
          <option value="roue">Roue</option>
          <option value="frein">Frein</option>
          <option value="chaine">Chaine</option>
          <option value="pedale">Pédale</option>
          <option value="autre">Autre</option>
        </select>
        <label *ngIf="problem.objet === 'autre'" for="autreProbleme">Si "Autre" :</label>
        <input *ngIf="problem.objet === 'autre'" type="text" name="autreProbleme" id="autreProbleme" [(ngModel)]="problem.autreProbleme">
        <label for="image">Photo du problème :</label>
        <input type="file" name="image" id="image" [(ngModel)]="problem.image">
        <label for="probleme">Résumé du problème :</label>
        <textarea name="probleme" id="probleme" cols="20" rows="10" [(ngModel)]="problem.resume"></textarea>
        <input type="submit" aria-label="Valider" value="Valider">
      </fieldset>
    </form>
  </figure>
</main>
